const TIMER_INTERVAL=setInterval(countdownToVacation,1e3);function countdownToVacation(){const e=(new Date).getTime(),n=new Date("December 20, 2024 18:00:00").getTime()-e;n<0&&countdownToVacationCompleted();const t=Math.floor(n/864e5),o=Math.floor(n%864e5/36e5),c=Math.floor(n%36e5/6e4),a=Math.floor(n%6e4/1e3);document.getElementById("days").innerHTML=t,document.getElementById("hours").innerHTML=o,document.getElementById("minutes").innerHTML=c,document.getElementById("seconds").innerHTML=a}function countdownToVacationCompleted(){clearInterval(TIMER_INTERVAL),document.getElementById("vacation-countdown").style.display="none",document.getElementById("header-text").innerText="Enjoy your vacation!"}countdownToVacation();