const TIMER_INTERVAL=setInterval(countdownToVacation,1e3);function countdownToVacation(){const NOW=(new Date).getTime(),VACATION_DATE=new Date("May 27, 2023 08:15:00").getTime(),TIME_LEFT=VACATION_DATE-NOW;TIME_LEFT<0&&countdownToVacationCompleted();const DAYS=Math.floor(TIME_LEFT/864e5),HOURS=Math.floor(TIME_LEFT%864e5/36e5),MINUTES=Math.floor(TIME_LEFT%36e5/6e4),SECONDS=Math.floor(TIME_LEFT%6e4/1e3);document.getElementById("days").innerHTML=DAYS,document.getElementById("hours").innerHTML=HOURS,document.getElementById("minutes").innerHTML=MINUTES,document.getElementById("seconds").innerHTML=SECONDS}function countdownToVacationCompleted(){clearInterval(TIMER_INTERVAL),document.getElementById("vacation-countdown").style.display="none",document.getElementById("header-text").innerText="Enjoy your vacation!"}countdownToVacation();