function countdownToBirthday(){const NOW=(new Date).getTime(),VACATION_DATE=new Date("May 27, 2023 08:15:00").getTime(),TIME_LEFT=VACATION_DATE-NOW;TIME_LEFT<0&&countdownToBirthdayCompleted();const DAYS=Math.floor(TIME_LEFT/864e5),HOURS=Math.floor(TIME_LEFT%864e5/36e5),MINUTES=Math.floor(TIME_LEFT%36e5/6e4),SECONDS=Math.floor(TIME_LEFT%6e4/1e3);document.getElementById("days").innerHTML=DAYS,document.getElementById("hours").innerHTML=HOURS,document.getElementById("minutes").innerHTML=MINUTES,document.getElementById("seconds").innerHTML=SECONDS}function countdownToBirthdayCompleted(){clearInterval(countdownToBirthday),document.getElementById("birthday-countdown").style.display="none"}countdownToBirthday(),setInterval(countdownToBirthday,1e3);